	this.maxRadius = 35;
	//particle opacity min/max
	this.minOpacity = .005;
	this.maxOpacity = .35;
	//particle speed min/max
	this.minSpeed = .05;
	this.maxSpeed = .75;
	//frames per second
	this.fps = 60;
